import React, { Component } from "react";
import Counter from "./counter";

class Counters extends Component {
  state = {
    counters: [
      { id: 1, value: 0 },
      { id: 2, value: 0 },
      { id: 3, value: 0 },
    ],
  };

  handleDelete(id) {
    this.setState({
      counters: this.state.counters.filter((counter) => counter.id != id),
    });
  }

  handleAdd() {
    const { counters } = this.state;
    console.log(this.state.counters.length);
    // this.setState({
    //   counters: counters.push({
    //     id: counters[counters.length - 1].id + 1,
    //     value: 0,
    //   }),
    // });
  }
  render() {
    return (
      <div>
        <button onClick={this.handleAdd} className="btn btn-success btn-sm m-2">
          Add
        </button>
        {this.state.counters.map((counter) => {
          return (
            <Counter
              key={counter.id}
              onDelete={() => this.handleDelete(counter.id)}
              value={counter.value}
              selected={true}
            />
          );
        })}
      </div>
    );
  }
}

export default Counters;
